import React, { useState, useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

// Main App Component - Contains all logic and sub-components
export default function App() {
    // State to manage which view is currently active (e.g., login, admin dashboard)
    const [view, setView] = useState('login'); // 'login', 'admin', 'voter', 'candidate'
    
    // State for managing the authentication modal
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalRole, setModalRole] = useState(''); // The role for which the modal is opened
    const [isLoginMode, setIsLoginMode] = useState(true); // Toggles between login/register in modal

    // Handlers for showing/hiding views and modals
    const showLoginModal = (role) => {
        setModalRole(role);
        setIsLoginMode(true);
        setIsModalOpen(true);
    };

    const handleLogout = () => {
        setView('login');
    };

    const handleAuthSuccess = (role) => {
        setView(role);
        setIsModalOpen(false);
    };

    // Main render logic: displays the correct view based on state
    const renderView = () => {
        switch (view) {
            case 'admin':
                return <AdminDashboard onLogout={handleLogout} />;
            case 'voter':
                return <VoterDashboard onLogout={handleLogout} />;
            case 'candidate':
                return <CandidateDashboard onLogout={handleLogout} />;
            case 'login':
            default:
                return <LoginView onRoleSelect={showLoginModal} />;
        }
    };

    return (
        <div className="bg-gray-100 text-gray-800 min-h-screen" style={{ fontFamily: "'Inter', sans-serif" }}>
            <div className="container mx-auto p-4 md:p-8">
                <Header />
                {renderView()}
            </div>
            {isModalOpen && (
                <AuthModal
                    role={modalRole}
                    isLogin={isLoginMode}
                    setIsLogin={setIsLoginMode}
                    onClose={() => setIsModalOpen(false)}
                    onAuthSuccess={() => handleAuthSuccess(modalRole)}
                />
            )}
        </div>
    );
}

// --- Sub-Components ---

// Header Component
const Header = () => (
    <header className="text-center mb-10">
        <h1 className="text-4xl font-bold text-gray-900">Secure E-Voting System</h1>
        <p className="text-lg text-gray-600 mt-2">Powered by Ethereum Blockchain & Solidity</p>
    </header>
);

// Login View Component
const LoginView = ({ onRoleSelect }) => (
    <div className="max-w-4xl mx-auto bg-white p-8 rounded-xl shadow-lg animate-fade-in">
        <h2 className="text-2xl font-semibold text-center mb-6">Welcome! Please select your role to login.</h2>
        <div className="grid md:grid-cols-3 gap-6 text-center">
            <RoleCard
                title="Admin"
                description="Manage elections, candidates, and view analytics."
                icon={<svg className="w-16 h-16 mx-auto text-indigo-500 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"></path></svg>}
                buttonText="Admin Login"
                buttonColor="bg-indigo-600 hover:bg-indigo-700"
                onClick={() => onRoleSelect('admin')}
            />
            <RoleCard
                title="Voter"
                description="Cast your vote securely and verify election results."
                icon={<svg className="w-16 h-16 mx-auto text-green-500 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M18 9v3m0 0v3m0-3h3m-3 0h-3m-2-5a4 4 0 11-8 0 4 4 0 018 0zM3 20a6 6 0 0112 0v1H3v-1z"></path></svg>}
                buttonText="Voter Login"
                buttonColor="bg-green-600 hover:bg-green-700"
                onClick={() => onRoleSelect('voter')}
            />
            <RoleCard
                title="Candidate"
                description="View your profile and track your vote count in real-time."
                icon={<svg className="w-16 h-16 mx-auto text-blue-500 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5.121 17.804A13.937 13.937 0 0112 16c2.5 0 4.847.655 6.879 1.804M15 10a3 3 0 11-6 0 3 3 0 016 0zm6 2a9 9 0 11-18 0 9 9 0 0118 0z"></path></svg>}
                buttonText="Candidate Login"
                buttonColor="bg-blue-600 hover:bg-blue-700"
                onClick={() => onRoleSelect('candidate')}
            />
        </div>
    </div>
);

// Reusable Role Card for the Login View
const RoleCard = ({ title, description, icon, buttonText, buttonColor, onClick }) => (
    <div className="p-6 border rounded-lg hover:shadow-xl transition-shadow">
        {icon}
        <h3 className="text-xl font-semibold mb-2">{title}</h3>
        <p className="text-gray-500 mb-4">{description}</p>
        <button onClick={onClick} className={`w-full text-white py-2 px-4 rounded-md transition-colors ${buttonColor}`}>
            {buttonText}
        </button>
    </div>
);

// Admin Dashboard Component
const AdminDashboard = ({ onLogout }) => {
    const chartRef = useRef(null);
    const chartInstance = useRef(null);

    useEffect(() => {
        if (chartInstance.current) {
            chartInstance.current.destroy();
        }
        const ctx = chartRef.current.getContext('2d');
        chartInstance.current = new Chart(ctx, {
            type: 'bar',
            data: {
                labels: ['Alice Johnson', 'Bob Williams', 'Charlie Brown'],
                datasets: [{
                    label: '# of Votes',
                    data: [350, 280, 240], // Placeholder data
                    backgroundColor: ['rgba(79, 70, 229, 0.8)', 'rgba(5, 150, 105, 0.8)', 'rgba(37, 99, 235, 0.8)'],
                    borderColor: ['rgba(79, 70, 229, 1)', 'rgba(5, 150, 105, 1)', 'rgba(37, 99, 235, 1)'],
                    borderWidth: 1
                }]
            },
            options: {
                scales: { y: { beginAtZero: true } },
                responsive: true,
                plugins: { legend: { position: 'top' }, title: { display: true, text: 'Candidate Performance' } }
            }
        });

        // Cleanup function to destroy chart instance on component unmount
        return () => {
            if (chartInstance.current) {
                chartInstance.current.destroy();
            }
        };
    }, []);

    return (
        <div className="animate-fade-in">
            <DashboardHeader title="Admin Dashboard" onLogout={onLogout} />
            <div className="grid md:grid-cols-3 gap-6">
                <div className="md:col-span-1 bg-white p-6 rounded-xl shadow-lg">
                    <h3 className="text-xl font-semibold mb-4 border-b pb-2">Election Management</h3>
                    <button className="w-full bg-indigo-600 text-white py-2 px-4 rounded-md hover:bg-indigo-700 mb-4">Create New Election</button>
                    <h4 className="font-semibold mb-2">Active Elections</h4>
                    <ul className="space-y-2"><li className="p-3 bg-gray-50 rounded-md">Presidential Election 2025</li></ul>
                </div>
                <div className="md:col-span-2 bg-white p-6 rounded-xl shadow-lg">
                    <h3 className="text-xl font-semibold mb-4 border-b pb-2">Live Analytics: Presidential Election 2025</h3>
                    <div className="grid md:grid-cols-2 gap-4 mb-6 text-center">
                        <div className="bg-indigo-100 p-4 rounded-lg"><p className="text-gray-600">Total Voters</p><p className="text-3xl font-bold">1,250</p></div>
                        <div className="bg-green-100 p-4 rounded-lg"><p className="text-gray-600">Votes Cast</p><p className="text-3xl font-bold">870</p></div>
                    </div>
                    <canvas ref={chartRef}></canvas>
                </div>
            </div>
        </div>
    );
};

// Voter Dashboard Component
const VoterDashboard = ({ onLogout }) => (
    <div className="animate-fade-in">
        <DashboardHeader title="Voter Dashboard" onLogout={onLogout} />
        <div className="bg-white p-8 rounded-xl shadow-lg">
            <h3 className="text-2xl font-semibold mb-4">Presidential Election 2025</h3>
            <p className="text-gray-600 mb-6">Please select a candidate to cast your vote. Your vote is secure and immutable on the blockchain.</p>
            <div className="space-y-4">
                <CandidateOption name="Alice Johnson - Party A" />
                <CandidateOption name="Bob Williams - Party B" />
                <CandidateOption name="Charlie Brown - Party C" />
            </div>
            <button className="mt-8 w-full bg-green-600 text-white py-3 px-4 rounded-md text-lg font-semibold hover:bg-green-700 transition-colors">Cast Vote</button>
        </div>
    </div>
);

const CandidateOption = ({ name }) => (
    <label className="flex items-center p-4 border rounded-lg cursor-pointer hover:bg-gray-50">
        <input type="radio" name="candidate" className="h-5 w-5 text-green-600 focus:ring-green-500" />
        <span className="ml-4 text-lg font-medium">{name}</span>
    </label>
);

// Candidate Dashboard Component
const CandidateDashboard = ({ onLogout }) => (
    <div className="animate-fade-in">
        <DashboardHeader title="Candidate Dashboard" onLogout={onLogout} />
        <div className="bg-white p-8 rounded-xl shadow-lg">
            <h3 className="text-2xl font-semibold">Welcome, Alice Johnson!</h3>
            <p className="text-gray-600 mb-6">You are a candidate in the "Presidential Election 2025".</p>
            <div className="bg-blue-100 p-6 rounded-lg text-center">
                <p className="text-xl text-blue-800">Your Current Vote Count</p>
                <p className="text-6xl font-bold text-blue-900 mt-2">350</p>
                <p className="text-sm text-gray-500 mt-2">(Updates in real-time from the blockchain)</p>
            </div>
        </div>
    </div>
);

// Reusable Dashboard Header
const DashboardHeader = ({ title, onLogout }) => (
    <div className="flex justify-between items-center mb-6">
        <h2 className="text-3xl font-bold">{title}</h2>
        <button onClick={onLogout} className="bg-red-500 text-white py-2 px-4 rounded-md hover:bg-red-600">Logout</button>
    </div>
);

// Authentication Modal Component
const AuthModal = ({ role, isLogin, setIsLogin, onClose, onAuthSuccess }) => {
    const capitalize = (str) => str.charAt(0).toUpperCase() + str.slice(1);

    const handleSubmit = (e) => {
        e.preventDefault();
        // This is a simulation. A real app would make an API call to a Java backend.
        if (isLogin) {
            console.log(`Logging in as ${role}...`);
            onAuthSuccess();
        } else {
            console.log(`Registering as ${role}...`);
            alert('Registration successful! Please login.');
            setIsLogin(true);
        }
    };
    
    return (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center animate-fade-in">
            <div className="bg-white p-8 rounded-xl shadow-2xl w-full max-w-md relative">
                <button onClick={onClose} className="absolute top-4 right-4 text-gray-500 hover:text-gray-800">
                    <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path></svg>
                </button>
                <h2 className="text-2xl font-bold text-center mb-6">{isLogin ? `${capitalize(role)} Login` : `${capitalize(role)} Registration`}</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label htmlFor="email" className="block text-sm font-medium text-gray-700">Email</label>
                        <input type="email" id="email" className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500" />
                    </div>
                    <div className="mb-6">
                        <label htmlFor="password" className="block text-sm font-medium text-gray-700">Password</label>
                        <input type="password" id="password" className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500" />
                    </div>

                    {!isLogin && role === 'voter' && <BiometricSection />}

                    <button type="submit" className="w-full bg-indigo-600 text-white py-2 px-4 rounded-md hover:bg-indigo-700 transition-colors">{isLogin ? 'Login' : 'Register'}</button>
                </form>
                <p className="text-center text-sm mt-4">
                    {isLogin ? "Don't have an account?" : "Already have an account?"}
                    <button onClick={() => setIsLogin(!isLogin)} className="font-medium text-indigo-600 hover:text-indigo-500 ml-1">
                        {isLogin ? 'Register' : 'Login'}
                    </button>
                </p>
            </div>
        </div>
    );
};

// Biometric Section for Voter Registration
const BiometricSection = () => (
    <div className="text-center mb-6">
        <p className="text-gray-600 mb-2">For security, we need to verify your identity.</p>
        <div className="w-40 h-40 bg-gray-200 rounded-lg mx-auto flex items-center justify-center">
            <svg className="w-16 h-16 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z"></path><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 13a3 3 0 11-6 0 3 3 0 016 0z"></path></svg>
        </div>
        <button type="button" className="mt-2 text-sm text-indigo-600 hover:underline">Capture Face ID</button>
        <p className="text-xs text-gray-500 mt-1">Simulating AI Face Detection via OpenCV</p>
    </div>
);
