                <StatCard icon={<UsersIcon className="w-6 h-6"/>} title="Registered Voters" value="12,300" trend="+5.2% this week" color="secondary" />
                <StatCard icon={<StarIcon className="w-6 h-6"/>} title="Total Candidates" value="12" trend="Verified" color="accent" />
