// --- Reusable UI Components ---

const RoleCard = ({ title, description, icon, buttonText, color, onClick }) => (
    <div className="glass-card group text-white transform hover:-translate-y-2 transition-all duration-300">
        <div className={`w-16 h-16 rounded-full flex items-center justify-center bg-${color} bg-opacity-10 text-${color} mb-6`}>
            {React.cloneElement(icon, { className: "w-8 h-8" })}
        </div>
        <h3 className="text-2xl font-semibold mb-3 text-white">{title}</h3>
        <p className="text-gray-300 mb-6 h-20">{description}</p>
        <button
            onClick={onClick}
            className={`w-full text-white font-semibold py-3 px-4 rounded-lg transition-all duration-300 bg-${color} hover:bg-${color}-hover shadow-lg hover:shadow-${color}/40`}
        >
            {buttonText}
        </button>
    </div>
);

const AnalyticsChart = () => {
    const chartRef = useRef(null);
    useEffect(() => {
        const ctx = chartRef.current.getContext('2d');
        const gradient1 = ctx.createLinearGradient(0, 0, 0, 400);
        gradient1.addColorStop(0, 'rgba(129, 140, 248, 0.6)');
        gradient1.addColorStop(1, 'rgba(129, 140, 248, 0)');
        const gradient2 = ctx.createLinearGradient(0, 0, 0, 400);
        gradient2.addColorStop(0, 'rgba(110, 231, 183, 0.6)');
        gradient2.addColorStop(1, 'rgba(110, 231, 183, 0)');

        const chartInstance = new Chart(ctx, {
            type: 'line',
            data: {
                labels: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'],
                datasets: [
                    {
                        label: 'Alice Johnson',
                        data: [120, 190, 300, 500, 200, 300, 412],
                        borderColor: '#818cf8',
                        backgroundColor: gradient1,
                        borderWidth: 3,
                        pointBackgroundColor: '#fff',
                        pointBorderColor: '#818cf8',
                        pointHoverRadius: 7,
                        tension: 0.4,
                        fill: true,
                    },
                    {
                        label: 'Bob Williams',
                        data: [80, 120, 210, 350, 150, 250, 280],
                        borderColor: '#6ee7b7',
                        backgroundColor: gradient2,
                        borderWidth: 3,
                        pointBackgroundColor: '#fff',
                        pointBorderColor: '#6ee7b7',
                        pointHoverRadius: 7,
                        tension: 0.4,
                        fill: true,
                    },
                ]
            },
            options: {
                scales: {
                    y: { beginAtZero: true, grid: { color: 'rgba(255, 255, 255, 0.1)' }, ticks: { color: '#e5e7eb' } },
                    x: { grid: { display: false }, ticks: { color: '#e5e7eb' } }
                },
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                     legend: { position: 'top', align: 'end', labels: { color: '#e5e7eb', boxWidth: 12 } }
                }
            }
        });
        return () => chartInstance.destroy();
    }, []);

    return <div className="h-80"><canvas ref={chartRef}></canvas></div>;
};

const RadialProgressChart = ({ value }) => {
    const chartRef = useRef(null);

    useEffect(() => {
        const chartInstance = new Chart(chartRef.current, {
            type: 'doughnut',
            data: {
                datasets: [{
                    data: [value, 100 - value],
                    backgroundColor: ['#fb923c', 'rgba(251, 146, 60, 0.2)'],
                    borderColor: 'transparent',
                    borderRadius: 20,
                    cutout: '80%',
                }]
            },
            options: {
                responsive: true,
                maintainAspectRatio: false,
                plugins: { legend: { display: false }, tooltip: { enabled: false } },
                circumference: 180,
                rotation: -90,
            }
        });
        return () => chartInstance.destroy();
    }, [value]);

    return (
        <div className="h-48 relative flex items-center justify-center">
            <canvas ref={chartRef}></canvas>
            <div className="absolute flex flex-col items-center justify-center pointer-events-none">
                <span className="text-4xl font-bold text-white">{value.toFixed(1)}%</span>
                <span className="text-sm text-gray-400">Turnout</span>
            </div>
        </div>
    );
};

const DashboardCard = ({ title, children, accentColor = 'transparent' }) => (
    <div className={`glass-card text-white border-t-2 border-${accentColor}`}>
        {title && <h3 className="text-xl font-bold mb-6 pb-4 border-b border-white/10">{title}</h3>}
        {children}
    </div>
);

const StatCard = ({ icon, title, value, trend, color }) => {
    const colorVariants = {
      primary: { bg: 'bg-primary', text: 'text-primary' },
      secondary: { bg: 'bg-secondary', text: 'text-secondary' },
      accent: { bg: 'bg-accent', text: 'text-accent' },
      yellow: { bg: 'bg-yellow-400', text: 'text-yellow-400' }
    };
    const selectedColor = colorVariants[color] || colorVariants.primary;

    return (
        <div className="glass-card p-5 flex items-center gap-4">
            <div className={`${selectedColor.bg} bg-opacity-10 w-12 h-12 rounded-lg flex items-center justify-center`}>
                <span className={selectedColor.text}>{icon}</span>
            </div>
            <div>
                <p className="text-gray-300 font-medium text-sm">{title}</p>
                <p className="text-2xl font-bold text-white">{value}</p>
                 <p className={`text-xs ${selectedColor.text} font-semibold flex items-center gap-1`}>
                    <TrendingUpIcon className="w-4 h-4" /> {trend}
                 </p>
            </div>
        </div>
    );
};


const CandidateOption = ({ name, party }) => (
    <label className="flex items-center p-5 border-2 border-white/10 rounded-lg cursor-pointer hover:bg-white/10 hover:border-secondary transition-colors duration-300 has-[:checked]:bg-secondary/20 has-[:checked]:border-secondary group">
        <img src={`https://i.pravatar.cc/40?u=${name.replace(' ', '')}`} alt={name} className="w-10 h-10 rounded-full" />
        <div className="ml-4 flex-grow">
            <span className="text-lg font-medium text-white">{name}</span>
            <p className="text-sm text-gray-400 group-has-[:checked]:text-secondary">{party}</p>
        </div>
        <input type="radio" name="candidate" className="h-5 w-5 text-secondary focus:ring-secondary-hover border-gray-500 bg-transparent" />
    </label>
);

const QuickActions = () => (
    <div className="grid grid-cols-2 gap-4">
        <ActionButton icon={<PlayCircleIcon />} label="Start Election" />
        <ActionButton icon={<StopCircleIcon />} label="End Election" />
        <ActionButton icon={<UserCheckIcon />} label="Verify Voter" />
        <ActionButton icon={<AwardIcon />} label="Publish Results" />
    </div>
);

const ActionButton = ({ icon, label }) => (
    <button className="flex flex-col items-center justify-center p-4 bg-white/5 rounded-lg border border-white/10 hover:bg-white/10 hover:border-primary transition-all duration-300 group">
        <div className="text-primary group-hover:text-white transition-colors">{icon}</div>
        <span className="mt-2 text-sm font-semibold text-gray-300 group-hover:text-white">{label}</span>
    </button>
)

const ManageCandidates = () => {
    const candidates = [
        { name: 'Alice Johnson', party: 'Unity Party', status: 'Verified' },
        { name: 'Bob Williams', party: 'Liberty Alliance', status: 'Verified' },
        { name: 'Charlie Brown', party: 'Future Forward', status: 'Pending' }
    ];
    return (
        <div className="space-y-3">
            {candidates.map(c => (
                <div key={c.name} className="flex items-center justify-between p-3 bg-white/5 rounded-lg hover:bg-white/10 transition-colors">
                     <div className="flex items-center gap-4">
                         <img src={`https://i.pravatar.cc/40?u=${c.name.replace(' ', '')}`} alt={c.name} className="w-10 h-10 rounded-full"/>
                         <div>
                             <p className="font-medium text-white">{c.name}</p>
                             <p className="text-sm text-gray-400">{c.party}</p>
                         </div>
                     </div>
                     <div className="flex items-center gap-4">
                        <span className={`text-xs font-bold px-2 py-1 rounded-full ${c.status === 'Verified' ? 'bg-green-500/20 text-green-400' : 'bg-yellow-500/20 text-yellow-400'}`}>
                           {c.status}
                        </span>
                        <button className="text-gray-400 hover:text-white"><MoreVerticalIcon /></button>
                     </div>
                </div>
            ))}
        </div>
    );
};


const LiveTransactionFeed = () => {
    const transactions = [
        { tx: '0x1a2b...', candidate: 'Alice Johnson', time: '2s ago' },
        { tx: '0x9f8c...', candidate: 'Bob Williams', time: '5s ago' },
        { tx: '0x3d4e...', candidate: 'Alice Johnson', time: '8s ago' },
    ];

    return (
        <div className="space-y-3 max-h-48 overflow-y-auto pr-2">
            {transactions.map((t, index) => (
                <div key={index} className="flex items-center justify-between p-2.5 bg-white/5 rounded-lg animate-fade-in" style={{animationDelay: `${index * 150}ms`}}>
                    <div className="flex items-center gap-3">
                         <div className="w-8 h-8 bg-primary/10 text-primary rounded-full flex items-center justify-center flex-shrink-0">
                            <CheckSquareIcon className="w-5 h-5" />
                        </div>
                        <div>
                            <p className="font-mono text-sm font-medium text-gray-300">{t.tx}</p>
                            <p className="text-xs text-gray-400">Vote for <span className="font-semibold text-white">{t.candidate}</span></p>
                        </div>
                    </div>
                    <p className="text-xs text-gray-500 flex-shrink-0 ml-2">{t.time}</p>
                </div>
            ))}
        </div>
    );
};


// --- Authentication Modal ---

const AuthModal = ({ role, isLogin, setIsLogin, onClose, onAuthSuccess }) => {
    const capitalize = (str) => str.charAt(0).toUpperCase() + str.slice(1);

    const handleSubmit = (e) => {
        e.preventDefault();
        onAuthSuccess();
    };

    return (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center p-4 animate-fade-in z-50">
            <div
                className="glass-card p-8 w-full max-w-md relative animate-scale-in text-white"
                role="dialog"
                aria-modal="true"
            >
                <button onClick={onClose} className="absolute top-4 right-4 text-gray-300 hover:text-white">
                    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><line x1="18" y1="6" x2="6" y2="18"></line><line x1="6" y1="6" x2="18" y2="18"></line></svg>
                </button>
                <div className="text-center mb-6">
                    <h2 className="text-2xl font-bold">{isLogin ? `${capitalize(role)} Login` : `${capitalize(role)} Registration`}</h2>
                    <p className="text-gray-300 mt-2">{isLogin ? 'Enter your credentials to access your dashboard.' : 'Create an account to get started.'}</p>
                </div>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <div>
                        <label htmlFor="email" className="block text-sm font-medium text-gray-200 mb-1">Email</label>
                        <input type="email" id="email" required className="glass-input" />
                    </div>
                    <div>
                        <label htmlFor="password" className="block text-sm font-medium text-gray-200 mb-1">Password</label>
                        <input type="password" id="password" required className="glass-input" />
                    </div>

                    {!isLogin && role === 'voter' && <BiometricSection />}

                    <button type="submit" className="w-full bg-primary text-white font-semibold py-3 px-4 rounded-lg hover:bg-primary-hover transition-colors duration-300 shadow-lg hover:shadow-primary/50">
                        {isLogin ? 'Login' : 'Create Account'}
                    </button>
                </form>
                <p className="text-center text-sm mt-6 text-gray-300">
                    {isLogin ? "Don't have an account?" : "Already have an account?"}
                    <button onClick={() => setIsLogin(!isLogin)} className="font-semibold text-white hover:underline ml-1">
                        {isLogin ? 'Register Now' : 'Login'}
                    </button>
                </p>
            </div>
        </div>
    );
};

const BiometricSection = () => (
    <div className="text-center p-4 bg-white/10 rounded-lg border border-white/20">
        <p className="font-semibold text-gray-200 mb-2">Biometric Identity Verification</p>
        <div className="w-32 h-32 bg-dark/50 border border-white/20 rounded-full mx-auto flex items-center justify-center mb-3">
            <CameraIcon />
        </div>
        <button type="button" className="font-semibold text-sm text-white hover:underline">
            Launch Secure Camera
        </button>
        <p className="text-xs text-gray-400 mt-1">Powered by OpenCV AI Face Detection</p>
    </div>
);
