// --- Authentication Modal ---

const AuthModal = ({ role, isLogin, setIsLogin, onClose, onAuthSuccess }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: Replace with backend auth
    console.log(`${isLogin ? 'Login' : 'Register'} as ${role}`, { email, password });
    onAuthSuccess(role);
  };

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
      <div className="bg-gray-900 p-8 rounded-xl shadow-2xl w-full max-w-md">
        <h2 className="text-2xl font-bold text-white mb-4">
          {isLogin ? 'Login' : 'Register'} as {role.charAt(0).toUpperCase() + role.slice(1)}
        </h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="email"
            placeholder="Email"
            className="glass-input w-full"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            className="glass-input w-full"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button
            type="submit"
            className="w-full bg-primary text-white font-semibold py-2 px-4 rounded-lg hover:bg-primary-hover"
          >
            {isLogin ? 'Login' : 'Register'}
          </button>
        </form>
        <p className="text-gray-400 text-sm mt-4 text-center">
          {isLogin ? "Don't have an account?" : "Already have an account?"}{' '}
          <button
            onClick={() => setIsLogin(!isLogin)}
            className="text-primary hover:underline"
          >
            {isLogin ? 'Register' : 'Login'}
          </button>
        </p>
        <button onClick={onClose} className="absolute top-4 right-4 text-gray-400 hover:text-white">
          
        </button>
      </div>
    </div>
  );
};


